import React from 'react';
import styles from ''

const OptionSize = ({ sizes, currentSize, handlerSize }) => {
  return (
    <ul>
      {sizes.map((size) => (
        <li key={size.name}>
          <button
            type="button"
            className={currentSize === size.name ? 'active' : ''}
            onClick={() => handlerSize(size.name)}
          >
            {size.name}
          </button>
        </li>
      ))}
    </ul>
  );
};

export default OptionSize;
