//import OptionColor from './OptionColor/OptionColor';
//import OptionSize from './OptionSize/OptionSize';
import Button from './Button/Button';
//import Product from '../Product';
import styles from './ProductFrom.module.scss';

const ProductForm = ({
  sizes,
  colors,
  currentSize,
  currentColor,
  handlerSize,
  handlerColor,
  summary,
  prepareColorClassName,
}) => {
  // eslint-disable-next-line no-unused-vars
  const getPrice = () => {
    const selectedSize = sizes.find((size) => size.name === currentSize);
    if (selectedSize) {
      // eslint-disable-next-line no-undef
      return basePrice + selectedSize.additionalPrice;
    }
    // eslint-disable-next-line no-undef
    return basePrice;
  };

  return (
    <form>
      <div className={styles.sizes}>
        <h3 className={styles.optionLabel}>Sizes</h3>
        <OptionSize sizes={sizes} currentSize={currentSize} handlerSize={handlerSize} />
        {/* <ul className={styles.choices}>
          {sizes.map((size) => (
            <li key={size.name}>
              <button
                type="button"
                className={currentSize === size.name ? 'active' : ''}
                onClick={() => handlerSize(size.name)}
              >
                {size.name}
              </button>
            </li>
          ))}
        </ul> */}
      </div>
      <div className={styles.colors}>
        <h3 className={styles.optionLabel}>Colors</h3>
        <ul className={styles.choices}>
          {colors.map((color) => (
            <li key={color}>
              <button
                type="button"
                className={prepareColorClassName(color) + (currentColor === color ? ' active' : '')}
                onClick={() => handlerColor(color)}
              ></button>
            </li>
          ))}
        </ul>
      </div>
      <Button onClick={summary}>
        <span className="fa fa-shopping-cart" />
      </Button>
    </form>
  );
};

export default ProductForm;
