import React, { useState } from 'react';
import styles from './Header.module.scss';
import Button from '../Button/Button';

const Header = () => {
  const [summaryInfo, setSummaryInfo] = useState(null);

  const handleAddToCart = () => {
    // Tutaj można dodać logikę związana z aktualizacją informacji o podsumowaniu
    const newSummaryInfo = {
      name: 'Product Name',
      price: 10.99,
      size: 'M',
      color: 'Blue',
    };
    setSummaryInfo(newSummaryInfo);
  };

  return (
    <header>
      <h1 className={styles.title}>ShopApp</h1>
      {summaryInfo && (
        <div>
          <span>Name: {summaryInfo.name}</span>
          <span>Price: {summaryInfo.price}</span>
          <span>Size: {summaryInfo.size}</span>
          <span>Color: {summaryInfo.color}</span>
        </div>
      )}
      <Button onClick={handleAddToCart}>Add to Cart</Button>
    </header>
  );
};

export default Header;
